import React, { Component, Fragment} from 'react'
import Map from '../components/map.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.pos=this.initialPosition()
  }
  componentDidMount() 
  {
    this.stores = this.getStores(this.pos)
  }

  async initialPosition()
  {
    let pos = {lat:4.603843 , lng: -74.062705}
    let ip = await fetch('https://api.ipify.org').then(response=>response.text())
    pos = await fetch('https://api.ipgeolocation.io/ipgeo?apiKey=02ff42e6f29c453e8d60df6139f5f9ed&ip='+ip).then(response=>response.json())
    return {lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}
  }

  getUserPosition()
  {
    let coordinates = null

    //Updated from browser
    if (navigator.geolocation) 
    {
      coordinates = navigator.geolocation.getCurrentPosition(function(position) {
        console.log(this.pos)
        return({
          lat: position.coords.latitude,
          lng: position.coords.longitude
        })
      }, function() {
        return null
      })
    }
    console.log(coordinates)
    return coordinates
  }

  async getStores(pos)
  {
    let url = 'https://front-iota.now.sh/api/stores?lat='+ pos.lat +'&lng='+pos.lng
    let stores = await fetch(url).then(response=>response.json())
    return stores.stores
  }

  render() {
    return (
      <div>
        <Map pos={this.pos} stores={this.stores}/>
      </div>
    );
  }
}

export default App;
