      {_id:"11", name:"Carnes Y Mas Carnes", address:"KR 6 166 -18", type:"stationeryStore", acceptsCards:true},
      {_id:"12", name:"Carnes Y Mas Carnes", address:"KR 6 166 -18", type:"store", acceptsCards:true},
