import React, { Component, Fragment} from 'react'
import Link from 'next/link'

import Paper from '@material-ui/core/Paper'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'

import FavoriteIcon from '@material-ui/icons/Favorite'
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder'
import StorefrontIcon from '@material-ui/icons/Storefront'
import CreditCardIcon from '@material-ui/icons/CreditCard'
import LocalShippingIcon from '@material-ui/icons/LocalShipping'


class fridgeMagnets extends Component {

  constructor(props)
  {
    super(props)
  }

  render() {
    const storeTypes = [
      "dryCleaner", 
      "stationeryStore", 
      "groceryStore", 
      "fastFoodRestaurant", 
      "restaurant", 
      "hardwareStore", 
      "petStore", 
      "liquorStore", 
      "vegetableStore", 
      "bakery", 
      "pharmacy",  
      "butchery"
    ]

    return (
      <div className="root">

        <style jsx>
          {`
            .magnet{
              text-align: center;
            }

            .sideButton{
              position: absolute;
              display: flex;
              align-items: center;
              justify-content: center;
              z-index:2;
            }

            .bottom{
              botom: 0;
              height: 40px;
              width: 50%; 
            }

            .top{
              left: 35px;
              top: -40px;
              height: 80px;
              width: 80px; 
              border-radius: 40px;
            }

            .left{
              background: gainsboro;
              border-bottom-left-radius: 15px;
              left: 0;
            }

            .right{
              background: lightgrey;
              border-bottom-right-radius: 15px;
              right: 0;
            }

            .butchery{
              background: lightpink;
            }

            .pharmacy{
              background: lightcyan;
            }

            .bakery{
              background: lavender;
            }

            .vegetableStore{
              background: green;
            }

            .liquorStore{
              background: deeppink;
            }

            .petStore{
              background: peru;
            }

            .hardwareStore{
              background: slategrey;
            }

            .restaurant{
              background: olivedrab;
            }

            .fastFoodRestaurant{
              background: tomato;
            }

            .groceryStore{
              background: gold;
            }

            .stationeryStore{
              background: steelblue;
            }

            .store{
              background: gainsboro;
            }

            .dryCleaner{
              background: skyblue;
            }
          `}
        </style>
          <Paper elevation={5} style={{width:"140px", marginTop:"50px", marginBottom:"5px", marginLeft:"5px", marginRight:"5px", paddingLeft:"5px", paddingRight:"5px", paddingTop:"45px", paddingBottom:"40px", position:'relative', borderRadius:'15px'}}>
          <div className={"sideButton top "+(this.props.store.type!=null && storeTypes.includes(this.props.store.type) ?this.props.store.type:"store")}>
              <StorefrontIcon fontSize="large"/> 
          </div>
          <div className="magnet">
              <p style={{height:"60px"}}>
              <strong>{this.props.store.name}</strong>
              <br></br>
              <small>{(this.props.store.type!=null?this.props.store.type:"Tienda")}</small>
              <br></br>
              <small>{(this.props.store.acceptsCards?<CreditCardIcon color=""/>+" Acepta Tarjetas":"")} </small>
              <br></br>
              <small>{this.props.store.address}</small>
              </p>
              <p>

              </p>
          </div>
          <div className="sideButton bottom left">
              <FormControlLabel
                  style={{marginRight:"-10px"}}
                  control={
                  <Checkbox 
                  icon={<FavoriteBorderIcon />} 
                  checkedIcon={<FavoriteIcon />} 
                  name="checkedH" 
                  />
              }
              />
          </div>
          <Link href="/about">
              <div className="sideButton bottom right">
              <LocalShippingIcon color=""/> 
              </div>
          </Link>
        </Paper>
      </div>
    )
  }
}

export default fridgeMagnets;
