import React, { useContext } from 'react'
import App from 'next/app'
import Router from 'next/router'
import UserContext from '../components/UserContext'
import AuthContext from '../components/authContext.js'

import '../styles.css'
import '@brainhubeu/react-carousel/lib/style.css'

export default class MyApp extends App {
  state = {
    token:null,
    user: null,
    location:null,
  }

  componentDidMount = () => {
    this.location()

    // const user = localStorage.getItem('user')
    // const token = localStorage.getItem('token')

    // if (user && token) {
    //   this.setState({
    //     user:user,
    //     token:token
    //   });
    // } else {
    //   Router.push('/signin');
    // }
  }

  location = async () => {
    await navigator.geolocation.getCurrentPosition(
      position => this.setState({ location:{
        lat: position.coords.latitude, 
        lng: position.coords.longitude
      }}), 
      err => async function()
      {
        let ip = await fetch(process.env.IPIFY_URL).then(response=>response.text())
        let pos = await fetch(process.env.IPGEOLOCATION_API_URL +'/ipgeo?apiKey='+process.env.IPGEOLOCATION_API_KEY+'&ip='+ip).then(response=>response.json())

        this.setState({ location:{lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}})
      }
    )
  }

  signIn = (username, password) => {
    localStorage.setItem('user', username);

    this.setState(
      {
        user: username
      },
      () => {
        Router.push('/');
      }
    );
  };

  signOut = () => {
    localStorage.removeItem('user');
    this.setState({
      user: null
    });
    Router.push('/signin');
  };

  render() {
    const { Component, pageProps } = this.props;

    return (
      <AuthContext>
        <UserContext.Provider value={{ user: this.state.user, signIn: this.signIn, signOut: this.signOut }}>
          <Component {...pageProps} />
        </UserContext.Provider>
      </AuthContext>
    );
  }
}
