import React from 'react'
import ReactDOM from 'react-dom'
import Head from 'next/head'
import AddressBar from '../components/addressBar.js'
import NoStores from '../components/noStores.js'

function getUser(props, state, map)
{
  if(state.userMarker != null)
  {
    state.userMarker.setMap(null);
  }

  let user = new google.maps.Marker({
    position: props.pos,
    icon: './map/user_icon.svg',
    title: "Tu ubicaci√≥n",
    draggable:false,
  })

  user.setMap(map);
  map.panTo(props.pos);

  return user
}

function getStores(props, state, map)
{
  let list = []
  if(Array.isArray(props.stores) && props.stores.length)
  {
    stores = props.stores
    for (let i = 0; i < stores.length; i++) {
      store = this.storeMarker(stores[i], map)
      list.push(store)
    }

  }
  else
  {
    for (let i = 0; i < state.stores.length; i++)
    {
      state.stores[i].setMap(null)
    }
  }

  return list
}

function storeMarker(store, map)
{
  let marker = null
  if(store.id == null || store.lat == null || store.lng == null || store.name == null)
  {
    console.log("Skiped store #"+i)
  }
  else
  {

    store.type = (store.type==null?"store":store.type.toLowerCase())
    let icon_normal = './map/' + store.type + "_icon.svg";
    let icon_hover = './map/' + store.type + "_icon_hover.svg";

    marker = new google.maps.Marker({
      position: {lat:store.lat, lng:store.lng},
      icon: icon_normal,
      title:store.name,
      draggable:false,
      id:store.id, //custom data
    })

    google.maps.event.addListener(marker, 'click', function() {
      console.log("click " +marker.id)       
    })

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    })

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon_normal);    
    })

    marker.setMap(map)
  }
  return marker
}

class Map extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      map: null,
      userMarker: null,
      stores: []
    }
  }

  static getDerivedStateFromProps(props, state) {
    let newMap = null
    let newUserMarker = null
    let newStores = []

    if(typeof google !== 'undefined' && props.pos != null && props.pos.lat != null && props.pos.lng != null) 
    {
      newMap = this.getMap(props, state)
      newUserMarker = this.getUser(props, state, newMap)
      newStores = this.getSores(props, state, newMap)
    }

    return {
      map: newMap,
      userMarker: newUserMarker,
      stores:newStores
    }
  }

  static getMap(props, state)
  {
    let map = null
    if(state.map == null)
    {
      map = new google.maps.Map(document.getElementById('map'), {
        center: props.pos,
        zoom: 15,
        mapTypeControl: false,
        fullscreenControl: false,
      })
    }
    else
    {
      map = state.map
    }

    return map
  }

  render () {
    let customStyle = {
      padding:"0",
      margin:"0",
      display: 'flex',
      height: '97vh',

      alignItems:'center', 
      justifyContent: 'center',

      background:'WhiteSmoke'
    }

    let imageStyle = {
      width: "50%",
      marginLeft:"auto",
      marginRight: "auto"
    }

    const handleOpen = () => {
      setOpen(true);
    };

    const handleClose = () => {
      setOpen(false);
    };

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div>
          <div id="map" style={customStyle}>
            <img src="/loading.svg" alt="Pajarito escuchando m√∫sica feliz" style={imageStyle}></img>
          </div>
          {this.state.map
            ?<AddressBar/>
            :""
          }
          {this.state.map && !this.state.stores.length
            ?<NoStores/>
            :""
          }
        </div>
      </div>
    )
  }
}

export default Map
