import React, { Component, Fragment} from 'react'
import Link from 'next/link'

import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'

import FavoriteIcon from '@material-ui/icons/Favorite'
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder'
import StorefrontIcon from '@material-ui/icons/Storefront';
import CreditCardIcon from '@material-ui/icons/CreditCard';
import LocalShippingIcon from '@material-ui/icons/LocalShipping';


class fridgeMagnets extends Component {

  constructor(props)
  {
    super(props)
  }

  render() {

    return (
      <div>

        <style jsx>
          {`
            horizontalScroll{
              width: 100px;
              height: 300px;
              overflow-y: auto;
              overflow-x: hidden;
            }
            .magnet{
              text-align: center;
            }

            .sideButton{
              position: absolute;
              display: flex;
              align-items: center;
              justify-content: center;
              z-index:2;
            }

            .bottom{
              botom: 0;
              height: 40px;
              width: 50%; 
            }

            .top{
              left: 35px;
              top: -40px;
              height: 80px;
              width: 80px; 
              border-radius: 40px;
            }

            .left{
              background: gainsboro;
              border-bottom-left-radius: 15px;
              left: 0;
            }

            .right{
              background: lightgrey;
              border-bottom-right-radius: 15px;
              right: 0;
            }

            .butchery{
              background: lightpink;
            }

            .pharmacy{
              background: lightcyan;
            }

            .bakery{
              background: lavender;
            }

            .vegetableStore{
              background: green;
            }

            .liquorStore{
              background: gainsboro;
            }

            .petStore{
              background: peru;
            }

            .hardwareStore{
              background: slategrey;
            }

            .reastaurant{
              background: olivedrab;
            }

            .fastFoodRestaurant{
              background: tomato;
            }

            .groceryStore{
              background: gold;
            }

            .stationeryStore{
              background: steelblue;
            }

            .store{
              background: gainsboro;
            }

            .dryCleaner{
              background: skyblue;
            }
          `}
        </style>

        <div className="horizontalScroll">
          {(
          this.props.stores==null
            ?""
            :this.props.stores.map((store) => (
              <Paper elevation={5} style={{display:'inline-block', width:"140px", marginTop:"50px", marginBottom:"5px", marginLeft:"5px", marginRight:"5px", paddingLeft:"5px", paddingRight:"5px", paddingTop:"45px", paddingBottom:"40px", position:'relative', borderRadius:'15px'}}>
                <div className={"sideButton top "+(store.type!=null?store.type:"store")}>
                  <StorefrontIcon fontSize="large"/> 
                </div>
                <div className="magnet">
                  <p style={{height:"60px"}}>
                    <strong>{store.name}</strong>
                    <br></br>
                    <small>{(store.type!=null?store.type:"Tienda")}{(store.aceptsCards?" - "+<CreditCardIcon color=""/>+" Acepta Tarjetas":"")} </small>
                    <br></br>
                    <small>{store.address}</small>
                  </p>
                  <p>

                  </p>
                </div>
                <div className="sideButton bottom left">
                  <FormControlLabel
                      style={{marginRight:"-10px"}}
                      control={
                      <Checkbox 
                        icon={<FavoriteBorderIcon />} 
                        checkedIcon={<FavoriteIcon />} 
                        name="checkedH" 
                      />
                    }
                  />
                </div>
                <Link href="/about">
                  <div className="sideButton bottom right">
                    <LocalShippingIcon color=""/> 
                  </div>
                </Link>
              </Paper>
            ))
          )}
        </div>
      </div>
    )
  }
}

export default fridgeMagnets;
