import React, { Component, Fragment} from 'react'
import Button from '@material-ui/core/Button';
import StorefrontIcon from '@material-ui/icons/Storefront'


class WellcomeSection extends Component {

  constructor(props)
  {
    super(props)
    this.state= {
      stores:null
    }
  }

  render() {

    return (
      <div>

        <style jsx>
          {`
            .root{
              height:250px;
              background:white;
              padding-left:15px;
              padding-right:15px;
              padding-top:0px;
              padding-bottom:10px;
              text-align:center;
            }
          `}
        </style>

        <div className="root">
            <h1>¡Hola{this.props.name}!</h1>
            <span>
              <small>Tu dirección: {this.props.address}</small>
              <br></br>
              <Button><small>Cambiar</small></Button>
            </span>

            <div style={{display: 'flex', alignItems: 'center', width:'100%'}}>
              <StorefrontIcon fontSize="large"/>
              <h2>&nbsp;{this.props.numStores}&nbsp;Tiendas</h2>
            </div>
            <span> a  menos de 1 km</span>
            <p>
              <Button
                variant="contained"
                color="primary"
                size="large"
                href="/search"
              >
                Hacer pedido  
              </Button>
            </p>
        </div>
      </div>
    )
  }
}

export default WellcomeSection;
