import React, { Component, createContext, useContext } from 'react';

const UserContext = createContext();

// create a provider
export class UserProvider extends Component {
    state = {
        user:null,
        position:null,
        isLoading:true,
    }

    componentDidMount() {
        this.location();
    }


    location = async () => {
        await navigator.geolocation.getCurrentPosition(
          position => this.setState({ location:{
            lat: position.coords.latitude, 
            lng: position.coords.longitude
          }}), 
          err => async function()
          {
            let ip = await fetch(process.env.IPIFY_URL).then(response=>response.text())
            let pos = await fetch(process.env.IPGEOLOCATION_API_URL +'/ipgeo?apiKey='+process.env.IPGEOLOCATION_API_KEY+'&ip='+ip).then(response=>response.json())

            this.setState({ location:{lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}, isLoading:false})
          }
        )
      }

    render() {
        const { location, userData} = this.state;
        const { children } = this.props

        const configObject = { 
            location, 
            userData
        }

        return (
            <UserContext.Provider value={configObject}>
                {children}
            </UserContext.Provider>
        );
    }
}

export default UserContext;
