import React, { Component, createContext, useContext } from 'react';
import AuthContext from './authContext.js';

const LocationContext = createContext();

// create a provider
export class LocationProvider extends Component {

  constructor(props)
  {
    super(props)
    this. state = {
      location:null,
      selectedLocation:null,
      featuredStores:null,
      isReady:true,
    }
  }

  componentDidMount() {
    this.location();
  }

  componentDidUpdate(prevProps, prevState) {
    if(this.state.selectedLocation != null && (prevState.selectedLocation != this.state.selectedLocation))
    {
      this.featuredStores(this.state.selectedLocation)
    }

    if(!this.state.isReady && this.state.selectedLocation !=null)
    {
      let now = new Date()
      let towait = 2000 - now.getTime() - this.startedAt.getTime()

      if(towait>0)
      {
        setTimeout(() => {
          this.setState({ isReady:true })
        }, towait)
      }
      else
      {
        this.setState({ isReady:true })
      }
    }
  }

  location = async () => {
    await navigator.geolocation.getCurrentPosition(
      position => this.setState({ 
        location:{
          lat: position.coords.latitude, 
          lng: position.coords.longitude
        },
        selectedLocation:{
          lat: position.coords.latitude, 
          lng: position.coords.longitude
        }
      }), 
      err => async function()
      {
        let ip = await fetch(process.env.IPIFY_URL).then(response=>response.text())
        let pos = await fetch(process.env.IPGEOLOCATION_API_URL +'/ipgeo?apiKey='+process.env.IPGEOLOCATION_API_KEY+'&ip='+ip).then(response=>response.json())

        this.setState({ 
          location:{lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}, 
          selectedLocation:{lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}
        })
      }
    )
  }

  async featuredStores(location)
  {
    let url = '/api/featuredStores?lat='+ location.lat +'&lng='+location.lng
    let res = await fetch(url).then(response=>response.json())

    this.setState({ featuredStores:res.stores })
  }

  render() {
    return (
      <LocationContext.Provider value={this.state}>
        {this.props.children}
      </LocationContext.Provider>
    );
  }
}

export default LocationContext;
