import React, { Component, Fragment} from 'react'
import Map from '../components/map.js'

var pos=null;

class App extends Component {
  constructor(props) {
    super(props)
    this.state = { 
      pos:null,
      stores:[]
    }
  }

  componentDidMount() {
    this.getPosition()
  }

  async getPosition()
  {
    let ip = await fetch('https://api.ipify.org').then(response=>response.text())
    let pos = await fetch('https://api.ipgeolocation.io/ipgeo?apiKey=02ff42e6f29c453e8d60df6139f5f9ed&ip='+ip).then(response=>response.json())

    let posFromIp = {lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}

    if (navigator.geolocation) 
    {
      let posFromBrowser = navigator.geolocation.getCurrentPosition(
        function(position) {
          let pos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          }
          console.log(pos)
          this.setState({ pos: pos })
        }, 
        function() {}
      )
    }
    else
    {
      this.setState({ pos: posFromIp })
    }
  }

  getUserPosition()
  {
    let coordinates = null

    //Updated from browser
    if (navigator.geolocation) 
    {
      coordinates = navigator.geolocation.getCurrentPosition(function(position) {
        console.log(this.pos)
        return({
          lat: position.coords.latitude,
          lng: position.coords.longitude
        })
      }, function() {
        return null
      })
    }
    console.log(coordinates)
    return coordinates
  }

  async getStores(pos)
  {
    let url = '/api/stores?lat='+ pos.lat +'&lng='+pos.lng
    let stores = await fetch(url).then(response=>response.json())
    return stores.stores
  }

  render() {
    return (
      <div>
        <Map pos={this.state.pos} stores={this.state.stores}/>
      </div>
    );
  }
}

export default App;
