import React from 'react';
import ReactDOM from 'react-dom';
import Head from 'next/head'

class Map extends React.Component {

  componentDidMount() 
  {
    this.initMap();
  }

  initMap()
  {
    this.map = new google.maps.Map(document.getElementById('map'), {
      center: this.props.pos,
      zoom: 15,
      mapTypeControl: false,
      fullscreenControl: false,
    });

    this.infoWindow = new google.maps.InfoWindow();

    if(this.props.userPos)
    {
      this.loadMapData(this.props.userPos);
    }
    else
    {
      this.loadMapData(this.props.pos);
    }
  }

  async loadMapData(pos, initial=true)
  {
    if(initial)
    {
      this.addMarker("user", pos.lat, pos.lng, "user", "Tu ubicación", null, null, null, null, true);
    }

    this.map.panTo(pos);
    let stores = this.props.stores;

    //TODO: issue#7 Agregar control que muestra mensaje "lo sentimos no hay tiendas cerca de tí, agrega la primera"
    for (let i = 0; i < stores.length; i++) {
      this.addMarker(stores[i].id, stores[i].lat, stores[i].lng, stores[i].type, stores[i].name, stores[i].address, stores[i].cellphone, stores[i].phone, stores[i].aceptsCreditCard);
    };
  }

  addMarker(id,lat, lng, markerType, name, address, cellphone, phone, aceptsCreditCard, draggable=false)
  {
    let icon_normal = './map/store_icon.svg';
    let icon_hover = './map/store_icon_hover.svg';

    if (markerType != null)
    {
      icon_normal = './map/' + markerType.toLowerCase() + "_icon.svg";
      icon_hover = './map/' + markerType.toLowerCase() + "_icon_hover.svg";
    }

    let marker = new google.maps.Marker({
        position: {lat:lat, lng:lng},
        icon: icon_normal,
        title:name,
        draggable:draggable,

        //custom data
        id:id,
        name:name,
        address:address,
        type:(markerType?markerType.toLowerCase():null),
        cellphone:cellphone,
        phone:phone,
        aceptsCreditCard: aceptsCreditCard
    });

    if(marker.id=="user")
    {
      this.infoWindow.setContent(
        '<div id="content">'+
          '<p>Arrastra este cursor para cargar las tiendas cercanas</p>'+
        '</div>'
      );
      this.infoWindow.open(map, marker); 
    }

    google.maps.event.addListener(marker, 'click', function() {
      if(marker.id != "user")
      {
        this.infoWindow.setContent(
          '<div id="content">'+
            '<h4 id="firstHeading" class="firstHeading">'+ marker.name +'</h4>'+
            '<p>'+ marker.address +'</p>'+
            (marker.aceptsCreditCard?'<p><i class="fa fa-credit-card"></i> Acepta tarjetas</p>':"")+
            '<div class="map-button-block">'+
              '<button class="map-button phone" type="submit" value="Call" onclick="callAction('+marker.phone+')"><i class="fa fa-phone"></i> Llamar</button>'+
              '<button class="map-button whatsapp" type="submit" value="Write" onclick="writeAction('+marker.cellphone+')"><i class="fa fa-whatsapp"></i> Escribir</button>'+
            '</div>'+
          '</div>'
        );
        this.infoWindow.open(map, marker); 
      }                     
    });

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    });

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon_normal);    
    });

    google.maps.event.addListener(marker, 'dragend', function(event) {
      this.loadMapData({ lat: Number(event.latLng.lat().toFixed(6)) , lng: Number(event.latLng.lng().toFixed(6)) }, false);
    });

    marker.setMap(this.map);
  }

  callAction(number)
  {
    console.log("call")
    // isCall = true;
    // callnumber = number;
    // if(!checkCookies())
    // {
    //   contactModal.style.display = "block";
    // }
    // else
    // {
    //   logUserActivity("call");
    //   window.open("tel:031"+callnumber);
    // }
  }

  writeAction(number)
  {
    console.log("write")
    // isCall = false;
    // callnumber=number;
    // if(!checkCookies())
    // {
    //   contactModal.style.display = "block";
    // }
    // else
    // {
    //   logUserActivity("write");
    //   window.open("https://wa.me/57"+callnumber+"?text=¡Hola!%20Vi%20tu%20tienda%20en%20Pideenlaesquina.com%20y%20quiero%20hacer%20un%20pedido.");
    // }
  }

  render () {
    var customStyle = {
      padding:"0",
      margin:"0",
      display: 'block',
      height: '97vh',

      background:'WhiteSmoke'
    }

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div id="map" style={customStyle}>Cargando...</div>
      </div>
    )
  }
}

export default Map
