    // }
  }

  render() {
    const auth0 = useContext(AuthContext);

    const { Component, pageProps } = this.props;

    return (
      <AuthContext.Provider>
        <UserContext.Provider value={{ user: this.state.user, location: this.state.location}}>
          <Component {...pageProps} />
        </UserContext.Provider>
      </AuthContext.Provider>
    );
  }
}
