import React from 'react'
import ReactDOM from 'react-dom'
import Head from 'next/head'

import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';
import Fab from '@material-ui/core/Fab';

import IconButton from '@material-ui/core/IconButton'
import SearchIcon from '@material-ui/icons/Search'
import AddLocationIcon from '@material-ui/icons/AddLocation'
import LocalShippingIcon from '@material-ui/icons/LocalShipping'
import PersonPinCircleIcon from '@material-ui/icons/PersonPinCircle'
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline'
import MyLocationIcon from '@material-ui/icons/MyLocation'

import NoStores from '../components/noStores.js'

const initialZoom = 15

class Map extends React.Component {

  constructor(props)
  {
    super(props)
    this.state = {
      map: null,
      userMarker:null,
      storeMarkers:[]
    }
  }

  shouldComponentUpdate() {
    if(this.props.pos == null)
    {
      return false
    }
    return true
  }

  componentDidUpdate(prevProps, prevState) 
  {
    if(this.props.pos != null && this.props.stores != null)
    {
      if(this.state.map == null)
      {
        let map=this.getMap(this.props.pos)
        let userMarker=this.placeUserMarker(map, this.props.pos)
        let storeMarkers = this.placeAllStoreMarkers(map, this.props.stores)

        this.setState ({
          userMarker:userMarker,
          map: map,
          storeMarkers: storeMarkers
        })
      }
      else if(prevProps.pos != this.props.pos)
      {
        let userMarker=this.placeUserMarker(this.state.map, this.props.pos)
        this.removeAllStoreMarkers()
        let storeMarkers = this.placeAllStoreMarkers(this.state.map, this.props.stores)

        this.setState ({
          userMarker:userMarker,
          storeMarkers: storeMarkers
        })
      }
    }
  }

  getMap(pos)
  {
    let map = null
    if(this.state.map == null)
    {
      map = new google.maps.Map(document.getElementById('map'), {
        center: pos,
        zoom: initialZoom,
        zoomControl:false,
        mapTypeControl: false,
        fullscreenControl: false,
        clickableIcons: false,
        streetViewControl: false
      })
    }
    else
    {
      map = this.state.map
    }
    return map
  }

  placeUserMarker(map, pos)
  {
    if(this.state.userMarker != null)
    {
      this.state.userMarker.setMap(null);
    }

    let marker = new google.maps.Marker({
      position: pos,
      icon: './map/user_icon.svg',
      title: "Tu ubicaci√≥n",
      draggable:false,
    })

    marker.setMap(map);
    this.panToPos(map, pos)

    return marker
  }

  panToPos(map, pos)
  {
    map.setZoom(initialZoom);
    map.panTo(pos);
  }

  placeAllStoreMarkers(map, stores)
  {
    let list = []

    for (let i = 0; i < stores.length; i++) {
      let store = stores[i]
      if(store._id == null || store.lat == null || store.lng == null || store.name == null)
      {
        console.log("Skiped store: "+store.name)
        continue
      }

      let marker = this.placeAStoreMarker(map, store)
      list.push(marker)
    }

    return list
  }

  removeAllStoreMarkers()
  {
    for (let i = 0; i < this.state.storeMarkers.length; i++)
    {
      this.state.storeMarkers[i].setMap(null)
    }
  }

  placeAStoreMarker(map, store)
  {
    store.type = (store.type==null?"store":store.type.toLowerCase())
    let icon_normal = './map/' + store.type + "_icon.svg";
    let icon_hover = './map/' + store.type + "_icon_hover.svg";

    let marker = new google.maps.Marker({
      position: {lat:store.lat, lng:store.lng},
      icon: icon_normal,
      title:store.name,
      draggable:false,
      id:store._id, //custom data
    })

    google.maps.event.addListener(marker, 'click', function() {
      console.log("click " +marker.id)       
    })

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    })

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon_normal);    
    })

    marker.setMap(map)
    return marker
  }

  render () {
    let customStyle = {
      padding:"0",
      margin:"0",
      display: 'flex',
      height: '97vh',
      flexDirection: "column-reverse"
    }

    let google_url = process.env.GOOGLE_MAPS_API_URL + "/js?key="+process.env.GOOGLE_MAPS_API_KEY

    return (
      <div>
        <Head>
          <script src= {google_url}></script>
        </Head>
        <div>
          <div style={customStyle}>
            <div id="actions" style={{background:'gold', height:"600px", width:"100%"}}>
              <Grid container spacing={3} style={{width:"80%", marginTop:"3%", marginLeft:"auto", marginRight:"auto"}}>
                <Grid item xs={12}>
                  <Paper elevation={3} style={{borderRadius:"20px", paddingRight:'10%', paddingLeft:'10%'}}>
                    <FormControl>
                      <InputLabel htmlFor="input-address">Ubicaci√≥n</InputLabel>
                      <Input
                        id="input-addres"
                        startAdornment={
                          <InputAdornment position="start">
                            <PersonPinCircleIcon />
                          </InputAdornment>
                        }
                      />
                    </FormControl>
                  </Paper>
                </Grid>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><SearchIcon /></Fab>
                </Grid>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><AddLocationIcon /></Fab>
                </Grid>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><LocalShippingIcon /></Fab>
                </Grid>
              </Grid>
              <Divider variant="middle" style={{marginTop:'5%', marginBottom:"5%"}}/>


              <Grid container spacing={3} style={{width:"80%", marginLeft:"auto", marginRight:"auto"}}>
                <span>Favoritas</span>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><AddCircleOutlineIcon /></Fab>
                </Grid>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><AddCircleOutlineIcon /></Fab>
                </Grid>
                <Grid item xs={4}>
                  <Fab size="large" aria-label="center"><AddCircleOutlineIcon /></Fab>
                </Grid>
              </Grid>

            </div>
            <div id="map" style={{background:"grey",height:"100%", width:"100%"}}>
              {this.state.map
              ?<Fab size="small" aria-label="center" style={{position: 'absolute', bottom:"5%", right:"5%"}} ><MyLocationIcon /></Fab>
              :""
              }
            </div>
          </div>

          {this.state.map && this.props.stores.length==0
            ?<NoStores/>
            :""
          }
        </div>
      </div>
    )
  }
}

export default Map
