import React, { Component, createContext } from 'react';
import LocationContext from "./LocationContext"

// create a provider
class LocationContextProvider extends Component {

  constructor(props)
  {
    super(props)
    this. state = {
      location_lat:null,
      location_lng: null,
      selected_location_lat: null,
      selected_location_lng: null,

      featuredStores:null,
      stores:null,
      isReady:false,
      isLoading:true
    }
    this.startedAt = new Date()
  }

  componentDidMount() {
    this.initialLocation();
  }

  componentDidUpdate(prevProps, prevState) {
    if(this.state.selectedLocation != null && (prevState.selectedLocation != this.state.selectedLocation))
    {
      this.featuredStores(this.state.selectedLocation)
      this.stores(this.state.selectedLocation)
    }

    if(
        !this.state.isReady 
        && this.state.selectedLocation !=null)
    {
      let now = new Date()
      let towait = 2000 - now.getTime() - this.startedAt.getTime()

      if(towait>0)
      {
        setTimeout(() => {
          this.setState({ isReady:true })
        }, towait)
      }
      else
      {
        this.setState({ isReady:true })
      }
    }

    if(this.state.isLoading 
      && this.state.stores != null 
      && this.state.featuredStores != null)
    {
      this.setState({ isLoading:false })
    }
  }

  initialLocation = async () => {
    await navigator.geolocation.getCurrentPosition(
      position => this.setState({ 
        location_lat:position.coords.latitude, 
        location_lng:position.coords.longitude,
        selected_location_lat:position.coords.latitude, 
        selected_location_lng: position.coords.longitude
      }), 
      err => async function()
      {
        let ip = await fetch(process.env.IPIFY_URL).then(response=>response.text())
        let pos = await fetch(process.env.IPGEOLOCATION_API_URL +'/ipgeo?apiKey='+process.env.IPGEOLOCATION_API_KEY+'&ip='+ip).then(response=>response.json())

        this.setState({ 
          location_lat:parseFloat(pos.latitude),
          location_lng:parseFloat(pos.longitude),
          selected_location_lat:parseFloat(pos.latitude),
          selected_location_lng:parseFloat(pos.longitude)
        })
      }
    )
  }

  updateLocation(newLat, newLng)
  {
    this.setState({ 
      location:{selected_location_lat:parseFloat(newLat), selected_location_lng:parseFloat(newLng)},
      isLoading: true 
    })
  }

  async featuredStores(lat, lng)
  {
    let url = '/api/featuredStores?lat='+ lat +'&lng='+lng
    let res = await fetch(url).then(response=>response.json())

    this.setState({ featuredStores:res.stores })
  }

  async stores(location)
  {
    let url = '/api/stores?lat='+ location.lat +'&lng='+location.lng
    let res = await fetch(url).then(response=>response.json())

    this.setState({ stores:res.stores })
  }

  render() {
    return (<LocationContext.Provider value={this.state}>{this.props.children}</LocationContext.Provider>);
  }
}

export default LocationContextProvider
