import React, { Component, Fragment} from 'react'
import Link from 'next/link'

import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'

import FavoriteIcon from '@material-ui/icons/Favorite'
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder'
import AddIcon from '@material-ui/icons/Add';
import CreditCardIcon from '@material-ui/icons/CreditCard';


class fridgeMagnets extends Component {

  constructor(props)
  {
    super(props)
  }

  render() {

    return (
      <div style={{backgroundImage: 'url("/fridge.svg")'}}>

        <style jsx>
          {`
            li {
              list-style: none;
              margin: 5px 0;
            }

            a {
              text-decoration: none;
              color: blue;
              font-family: 'Arial';
            }

            a:hover {
              opacity: 0.6;
            }
          `}
        </style>

        <Grid container justify="center" alignItems="baseline">
          {(
          this.props.stores==null
            ?""
            :this.props.stores.map((store) => (
              <Paper elevation={3} style={{width:"250px", margin:"5px", paddingLeft:"15px", paddingRight:"15px", paddingTop:"0", paddingBottom:"0", position:'relative', borderRadius:'15px'}}>
                <div style={{maxWidth:'200px'}}>
                  <p>
                    <strong>{store.name}</strong>
                    <br></br>
                    <small>{(store.type!=null?store.type:"Tienda")}{(store.aceptsCards?" - "+<CreditCardIcon color=""/>+" Acepta Tarjetas":"")} </small>
                  </p>
                  <p>
                    <small>{store.address}</small>
                  </p>
                </div>
                <div 
                  style={{
                    background:"gainsboro", 
                    position:"absolute", 
                    right:"0", 
                    top:"0", 
                    width:'40px', 
                    height:"50%", 
                    borderTopRightRadius:"15px",
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                  }}
                >
                  <FormControlLabel
                      style={{marginRight:"-10px"}}
                      control={
                      <Checkbox 
                        icon={<FavoriteBorderIcon />} 
                        checkedIcon={<FavoriteIcon />} 
                        name="checkedH" 
                      />
                    }
                  />
                </div>
                <Link href="/about">
                  <div 
                    style={{
                      background:"lightgrey", 
                      position:"absolute", 
                      right:"0", 
                      bottom:"0", 
                      width:'40px', 
                      height:"50%", 
                      borderBottomRightRadius:"15px",
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <AddIcon color=""/> 
                  </div>
                </Link>
              </Paper>
            ))
          )}
        </Grid>
      </div>
    )
  }
}

export default fridgeMagnets;
