import React from 'react';
import ReactDOM from 'react-dom';
import Head from 'next/head'

class Map extends React.Component {

  componentDidUpdate()
  {
    if (this.map == null)
    {
      this.initMap();
    }
    else{
      if(this.props.pos != null && this.props.pos.lng != null && this.props.lat != null)
      {
        this.loadMapData(this.props.pos, false)
      }
    }
  }

  initMap()
  {
    this.setState({map: new google.maps.Map(document.getElementById('map'), {
      center: this.props.pos,
      zoom: 15,
      mapTypeControl: false,
      fullscreenControl: false,
    })});

    this.infoWindow = new google.maps.InfoWindow();

    this.addMarker("user", pos.lat, pos.lng, "user", "Tu ubicación", null, null, null, null);

  }

  setUserMarker()
  {
    if(this.state.userMarker != null)
    {
      this.state.userMarker.setMap(null);
    }

    if(this.map != null)
    {
      this.setState({userMarker: new google.maps.Marker({
        position: this.props.pos,
        icon: './map/user_icon.svg',
        title: "Tu ubicación",
        draggable:false,
      })});

      this.state.userMarker.setMap(this.state.map);
      this.map.panTo(this.props.pos);
    }
  }

  setStoresMarkers()
  {
    if (this.props.stores.lenght == 0)
    {
      this.infoWindow.setContent(
        '<div id="content">'+
          '<h4 id="firstHeading" class="firstHeading">'+ marker.name +'</h4>'+
          '<p>'+ marker.address +'</p>'+
          (marker.aceptsCreditCard?'<p><i class="fa fa-credit-card"></i> Acepta tarjetas</p>':"")+
          '<div class="map-button-block">'+
            '<button class="map-button whatsapp" type="submit" value="Write" onclick="writeAction('+marker.cellphone+')"><i class="fa fa-whatsapp"></i> Escribir</button>'+
          '</div>'+
        '</div>'
      );
      this.infoWindow.open(map, this.state.userMarker); 
    }
    else
    {
      for (let i = 0; i < stores.length; i++) {
        this.addMarker(stores[i].id, stores[i].lat, stores[i].lng, stores[i].type, stores[i].name, stores[i].address, stores[i].cellphone, stores[i].phone, stores[i].aceptsCreditCard);
      };
    }
  }

  addMarker(id,lat, lng, markerType, name, address, cellphone, phone, aceptsCreditCard)
  {
    let icon_normal = './map/store_icon.svg';
    let icon_hover = './map/store_icon_hover.svg';

    if (markerType != null)
    {
      icon_normal = './map/' + markerType.toLowerCase() + "_icon.svg";
      icon_hover = './map/' + markerType.toLowerCase() + "_icon_hover.svg";
    }

    let marker = new google.maps.Marker({
        position: {lat:lat, lng:lng},
        icon: icon_normal,
        title:name,
        draggable:false,

        //custom data
        id:id,
        name:name,
        address:address,
        type:(markerType?markerType.toLowerCase():null),
        cellphone:cellphone,
        phone:phone,
        aceptsCreditCard: aceptsCreditCard
    });

    google.maps.event.addListener(marker, 'click', function() {
      if(marker.id != "user")
      {
        this.infoWindow.setContent(
          '<div id="content">'+
            '<h4 id="firstHeading" class="firstHeading">'+ marker.name +'</h4>'+
            '<p>'+ marker.address +'</p>'+
            (marker.aceptsCreditCard?'<p><i class="fa fa-credit-card"></i> Acepta tarjetas</p>':"")+
            '<div class="map-button-block">'+
              '<button class="map-button whatsapp" type="submit" value="Write" onclick="writeAction('+marker.cellphone+')"><i class="fa fa-whatsapp"></i> Escribir</button>'+
            '</div>'+
          '</div>'
        );
        this.infoWindow.open(map, marker); 
      }                     
    });

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    });

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon_normal);    
    });

    marker.setMap(this.map);
  }

  render () {
    var customStyle = {
      padding:"0",
      margin:"0",
      display: 'block',
      height: '97vh',

      background:'WhiteSmoke'
    }

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div id="map" style={customStyle}>Cargando...</div>
      </div>
    )
  }
}

export default Map
