import React from 'react'
import ReactDOM from 'react-dom'
import Head from 'next/head'
import AddressBar from '../components/addressBar.js'
import NoStores from '../components/noStores.js'

class Map extends React.Component {

  constructor(props) {
    super(props)
  }

  shouldComponentUpdate() {
    if(this.props.pos === null)
    {
      return false
    }
    return true
  }

  componentDidUpdate()
  {
    this.map=this.getMap()
    this.userMarker=this.placeUserMarker(map, this.props.pos)
    this.removeAllStoreMarkers()
    this.storeMarkers = this.placeAllStoreMarkers(map, this.props.stores)
  }

  getMap()
  {
    let map = null
    if(this.map == null)
    {
      map = new google.maps.Map(document.getElementById('map'), {
        center: this.props.pos,
        zoom: 15,
        mapTypeControl: false,
        fullscreenControl: false,
        clickableIcons: false,
        streetViewControl: false,
      })
    }
    else
    {
      map = this.map
    }

    return map
  }

  placeUserMarker(map, pos)
  {
    if(this.userMarker != null)
    {
      this.state.userMarker.setMap(null);
    }

    let marker = new google.maps.Marker({
      position: pos,
      icon: './map/user_icon.svg',
      title: "Tu ubicaci√≥n",
      draggable:false,
    })

    marker.setMap(map);
    this.panToPos(map, pos)
    return marker
  }

  panToPos(map, pos)
  {
    map.panTo(this.props);
  }

  placeAllStoreMarkers(map, stores)
  {
    let storeMarkers = []

    for (let i = 0; i < stores.length; i++) {
      let store = storeList[i]
      if(store._id == null || store.lat == null || store.lng == null || store.name == null)
      {
        console.log("Skiped store: "+store.name)
        continue
      }

      let marker = placeAStoreMarker(map, store)
      storeMarkers.push(marker)
    }

    return storeMarkers
  }

  removeAllStoreMarkers()
  {
    for (let i = 0; i < this.state.storeMarkers.length; i++)
    {
      props.stores[i].setMap(null)
    }
  }

  placeAStoreMarker(map, store)
  {
    store.type = (store.type==null?"store":store.type.toLowerCase())
    let icon_normal = './map/' + store.type + "_icon.svg";
    let icon_hover = './map/' + store.type + "_icon_hover.svg";

    let marker = new google.maps.Marker({
      position: {lat:store.lat, lng:store.lng},
      icon: icon_normal,
      title:store.name,
      draggable:false,
      id:store.id, //custom data
    })

    google.maps.event.addListener(marker, 'click', function() {
      console.log("click " +marker.id)       
    })

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    })

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon_normal);    
    })

    marker.setMap(map)
    return marker
  }

  render () {
    let customStyle = {
      padding:"0",
      margin:"0",
      display: 'flex',
      height: '97vh',

      alignItems:'center', 
      justifyContent: 'center',

      background:'WhiteSmoke'
    }

    let imageStyle = {
      width: "50%",
      marginLeft:"auto",
      marginRight: "auto"
    }

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div>
          <div id="map" style={customStyle}>
            <img src="/loading.svg" alt="Pajarito escuchando m√∫sica feliz" style={imageStyle}></img>
          </div>
          {this.state.map
            ?<AddressBar/>
            :""
          }
          {this.state.map && this.props.stores.length==0
            ?<NoStores/>
            :""
          }
        </div>
      </div>
    )
  }
}

export default Map
