import React, { Component, createContext } from 'react';

import Context from "./Context"
import createAuth0Client from '@auth0/auth0-spa-js';

// create a provider
class ContextProvider extends Component {

  constructor(props)
  {
    super(props)
    this. state = {
      locationLat:null,
      locationLng: null,

      featuredStores:null,
      stores:null,

      authClient: null,
      isAuthenticated: false,
      user:null,

      isReady:false
    }

    this.startedAt = new Date()
  }

  componentDidMount() {
    const dataLat = localStorage.getItem('_locationLat')
    const dataLng = localStorage.getItem('_locationLng')

    if(dataLat && dataLng) {
      this.setState({
        locationLat:dataLat, 
        locationLng:dataLng,
      })
    }
    else{
      this.initialLocation()
    }

    const dataFeaturedStores = localStorage.getItem('_featuredStores')
    if(dataFeaturedStores) {
      this.setState({featuredStores:data})
    }

    const dataStores = localStorage.getItem('_stores')
    if(dataStores) {
      this.setState({stores:data})
    }

    this.initializeAuth0()
  }

  componentDidUpdate(prevProps, prevState) {
    console.log("--------------")
    if(this.state.selectedLocationLat != null 
      && this.state.selectedLocationLng != null 
      && (
        (prevState.selectedLocationLat != this.state.selectedLocationLat)) 
        || (prevState.selectedLocationLng != this.state.selectedLocationLng)
      )
    {
      console.log("Location change")
      this.featuredStores(this.state.selectedLocationLat, this.state.selectedLocationLng)
      this.stores(this.state.selectedLocationLat, this.state.selectedLocationLng)
    }

    console.log(!this.state.isReady )
    console.log(this.state.selectedLocationLat !=null)
    console.log(this.state.selectedLocationLng !=null)
    console.log(this.state.stores != null)
    console.log(this.state.featuredStores != null)
    console.log(this.state.authClient!=null)
    if(
        !this.state.isReady 
        && this.state.selectedLocationLat !=null
        && this.state.selectedLocationLng !=null
        && this.state.stores != null
        && this.state.featuredStores != null
        && this.state.authClient!=null)
    {
      console.log("Initial location -> isReady=true")
      let now = new Date()
      let towait = 2000 - (now.getTime() - this.startedAt.getTime())

      if(towait>0)
      {
        setTimeout(() => {
          this.setState({ isReady:true })
        }, towait)
      }
      else
      {
        this.setState({ isReady:true })
      }
    }
  }

  initialLocation = async () => {
    await navigator.geolocation.getCurrentPosition(
      position => function() {
        this.setState({ 
          location:{
            lat:position.coords.latitude, 
            lng:position.coords.longitude
          },
        })

        localStorage.setItem('_locationLat', position.coords.latitude)  
        localStorage.setItem('_locationLng', position.coords.longitude)
      }, 
      err => async function()
      {
        let ip = await fetch(process.env.IPIFY_URL).then(response=>response.text())
        let pos = await fetch(process.env.IPGEOLOCATION_API_URL +'/ipgeo?apiKey='+process.env.IPGEOLOCATION_API_KEY+'&ip='+ip).then(response=>response.json())

        this.setState({ 
          locationLat:parseFloat(pos.latitude),
          locationLng:parseFloat(pos.longitude),
          selectedLocationLat:parseFloat(pos.latitude),
          selectedLocationLng:parseFloat(pos.longitude)
        })

        localStorage.setItem('_locationLat', pos.latitude)  
        localStorage.setItem('_locationLng', pos.longitude)
        localStorage.setItem('_selectedLocationLat', pos.latitude)  
        localStorage.setItem('_selectedLocationLng', pos.longitude)
      }
    )
  }

  updateLocation(newLat, newLng)
  {
    this.setState({ 
      selectedLocationLat:parseFloat(newLat), 
      selectedLocationLng:parseFloat(newLng)
    })

    localStorage.setItem('_selectedLocationLat', newLat)  
    localStorage.setItem('_selectedLocationLng', newLng)  
  }

  initializeAuth0 = async () => {
    const config = {
      domain: 'acacerca.auth0.com',//process.env.AUTH0_DOMAIN,
      client_id: 'd8Pv88MjaYWNSUKUHnO9JcudrUPZ6THl',//process.env.AUTH0_CLIENT_ID,
      redirect_uri: window.location.origin,
      cacheLocation: 'localstorage'
    }

    const auth0Client = await createAuth0Client(config)
    const isAuthenticated = await auth0Client.isAuthenticated()
    const user = isAuthenticated ? await auth0Client.getUser() : null

    this.setState({ authClient:auth0Client, isAuthenticated, user })
  };

  async featuredStores(lat, lng)
  {
    let url = '/api/featuredStores?lat='+ lat +'&lng='+lng
    let res = await fetch(url).then(response=>response.json())

    this.setState({ featuredStores:res.stores })
    localStorage.setItem('_featuredStores', position.coords.latitude)  
  }

  async stores(lat, lng)
  {
    let url = '/api/stores?lat='+ lat +'&lng='+lng
    let res = await fetch(url).then(response=>response.json())

    this.setState({ stores:res.stores })
    localStorage.setItem('_stores', position.coords.latitude)  
  }

  render() {
    const { 
      location_lat,
      location_lng,
      selectedLocationLat,
      selectedLocationLng,

      featuredStores,
      stores,

      authClient,
      isAuthenticated,
      user,

      isReady
    } = this.state;

    const values = { 
      location_lat,
      location_lng,
      selectedLocationLat,
      selectedLocationLng,

      featuredStores,
      stores,

      isAuthenticated,
      user,

      isReady,

      loginWithRedirect: (...p) => authClient.loginWithRedirect(...p),
      getTokenSilently: (...p) => authClient.getTokenSilently(...p),
      getIdTokenClaims: (...p) => authClient.getIdTokenClaims(...p),
      logout: (...p) => authClient.logout(...p)
    }

    return (<Context.Provider value={values}>{this.props.children}</Context.Provider>);
  }
}

export default ContextProvider
