import React, { Component, Fragment} from 'react'
import Map from '../components/map.js'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = { 
      pos:null,
      stores:[],
      loading:true
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.position()
    }, 1500)
  }

  componentDidUpdate()
  {
    if (this.state.pos !== null && this.state.stores.length==0)
    {
      this.getStores(this.state.pos)
    }
  }

  position = async () => {
    console.log(this.state.pos)
    await navigator.geolocation.getCurrentPosition(
      position => async function()
      {
        console.log(position.coords.latitude)
        this.setState({ pos:{
          lat: parseFloat(position.coords.latitude), 
          lng: parseFloat(position.coords.longitude)
        }})
      },
      err => async function()
      {
        let ip = await fetch('https://api.ipify.org').then(response=>response.text())
        let pos = await fetch('https://api.ipgeolocation.io/ipgeo?apiKey=02ff42e6f29c453e8d60df6139f5f9ed&ip='+ip).then(response=>response.json())

        this.setState({ pos:{lat:parseFloat(pos.latitude), lng:parseFloat(pos.longitude)}, stores:[] })
      }
    )
    console.log(this.state.pos)
  }

  async getStores(pos)
  {
    let url = '/api/stores?lat='+ pos.lat +'&lng='+pos.lng
    let stores = await fetch(url).then(response=>response.json())

    //this.setState({ stores:stores.stores })
  }

  render() {
    return (
      <div>
        <Map pos={this.state.pos} stores={this.state.stores}/>
      </div>
    );
  }
}

export default App;
