import React, { Component, Fragment} from 'react'
import Button from '@material-ui/core/Button';
import StorefrontIcon from '@material-ui/icons/Storefront'


class WellcomeSection extends Component {

  constructor(props)
  {
    super(props)
    this.state= {
      stores:null
    }
  }

  render() {

    const conTiendas = (
      <div>
        <p>
          <Button
            variant="contained"
            color="primary"
            size="large"
            href="/search"
          >
            Hacer pedido  
          </Button>
        </p>
      </div>
    )

    const sinTiendas = (
      <div>
        <h3><small>pero te tenemos cubierto...</small></h3>
        <p>Puedes hacer un pedido en cualquier establecimiento incluso si no está listado, nosotros nos encargamos.</p>
        <p><small>(también puedes cambiar de úbicación ingresando una nueva dirección)</small></p>
        <Button variant="contained" color="primary"><small>Hacer pedido</small></Button>
      </div>
    )

    return (
      <div>

        <style jsx>
          {`
            .root{
              height:350px;
              background:white;
              padding-left:15px;
              padding-right:15px;
              padding-top:0px;
              padding-bottom:10px;
              text-align:center;
            }
          `}
        </style>

        <div className="root">
            <h1>¡Hola{this.props.name}!</h1>
            <span>
              <small>Tu dirección: {this.props.address}</small>
              <br></br>
              <Button><small>Cambiar</small></Button>
            </span>
            <div style={{display: 'flex', flexDirection:"column", justifyContent: 'center', alignItems: 'center', width:'100%', marginTop:'10px'}}>
              <StorefrontIcon fontSize="large"/>
              <h2 style={{marginBottom:'0', marginTop:'5px'}}>&nbsp;{this.props.numStores}&nbsp;Tiendas</h2>
              <span> a  menos de 1 km</span>
            </div>
            {(this.props.numStores>0?conTiendas:sinTiendas)}
        </div>
      </div>
    )
  }
}

export default WellcomeSection;
