import React from 'react';
import ReactDOM from 'react-dom';
import Head from 'next/head'

class Map extends React.Component {

  componentDidUpdate()
  {
    if (this.map == null)
    {
      this.initMap();
    }
    else{
      if(this.props.pos != null && this.props.pos.lng != null && this.props.lat != null)
      {
        this.loadMapData(this.props.pos, false)
      }
    }
  }

  initMap()
  {
    this.setState({map: new google.maps.Map(document.getElementById('map'), {
      center: this.props.pos,
      zoom: 15,
      mapTypeControl: false,
      fullscreenControl: false,
    })});

    this.infoWindow = new google.maps.InfoWindow();

    this.addMarker("user", pos.lat, pos.lng, "user", "Tu ubicación", null, null, null, null);

  }

  setUserMarker()
  {
    if(this.state.userMarker != null)
    {
      this.state.userMarker.setMap(null);
    }

    if(this.map != null)
    {
      this.setState({userMarker: new google.maps.Marker({
        position: this.props.pos,
        icon: './map/user_icon.svg',
        title: "Tu ubicación",
        draggable:false,
      })});

      this.state.userMarker.setMap(this.state.map);
      this.map.panTo(this.props.pos);
    }
  }

  setStoresMarkers()
  {
    for (let i = 0; i < stores.length; i++) {
      store = stores[i]

      if (store.markerType == null)
      {
        store.markerType = "store"
      }

      let icon_normal = './map/' + markerType.toLowerCase() + "_icon.svg";
      let icon_hover = './map/' + markerType.toLowerCase() + "_icon_hover.svg";

      let marker = new google.maps.Marker({
        position: {lat:lat, lng:lng},
        icon: icon_normal,
        title:name,
        draggable:false,

        //custom data
        id:id,
        name:name,
        address:address,
        type:(markerType?markerType.toLowerCase():null),
        cellphone:cellphone,
        phone:phone,
        aceptsCreditCard: aceptsCreditCard
      });

      google.maps.event.addListener(marker, 'click', function() {
        console.log("click " +marker.id)       
      });

      google.maps.event.addListener(marker, 'mouseover', function() {
        marker.setIcon(icon_hover);
      });

      google.maps.event.addListener(marker, 'mouseout', function() {
        marker.setIcon(icon_normal);    
      });

      marker.setMap(this.map);
    }
  }

  render () {
    var customStyle = {
      padding:"0",
      margin:"0",
      display: 'block',
      height: '97vh',

      background:'WhiteSmoke'
    }

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div id="map" style={customStyle}>Cargando...</div>
      </div>
    )
  }
}

export default Map
