import React from 'react'
import Head from 'next/head'
import distance from './Distance'

const initialZoom = 15

class Map extends React.Component {

  constructor(props)
  {
    super(props)
    this.state = {
      map: null,
      userMarker:null,
      storeMarkers:[],
      selectedStore: null
    }
  }

  shouldComponentUpdate() {
    if(this.props.pos == null)
    {
      return false
    }
    return true
  }

  componentDidMount()
  {
    this.setState({map:null})
  }

  componentDidUpdate(prevProps, prevState) 
  {
    console.log("hola")
    if(this.props.pos !== null && (this.props.stores !== null || this.props.selectedStore !== null) )
    {
      console.log("hola-1")
      if(this.state.map === null)
      {
        console.log("hola-2")

      }

      if(this.state.map !== null && prevProps.pos !== this.props.pos)
      {
        let userMarker=this.placeUserMarker(this.state.map, this.props.pos)
        this.removeAllStoreMarkers()

        this.setState ({
          userMarker:userMarker
        })
      }

      if(this.state.map !== null && prevProps.stores !== this.props.stores)
      {
        this.removeAllStoreMarkers()
        let storeMarkers = this.placeAllStoreMarkers(this.state.map, this.props.stores)

        this.setState ({
          storeMarkers: storeMarkers
        })
      }

      if(this.state.map !== null && prevProps.selectedStore !== this.props.selectedStore)
      {
        this.removeSelectedStoreMarker()
        let selectedStore = this.placeAStoreMarker(this.state.map, this.props.selectedStore)

        this.setState ({
          selectedStore: selectedStore
        })
      }
    }
  }

  getMap(pos)
  {
    let map = null
    if(this.state.map == null)
    {
      map = new google.maps.Map(document.getElementById('map'), {
        center: pos,
        zoom: initialZoom,
        zoomControl:false,
        mapTypeControl: false,
        fullscreenControl: false,
        clickableIcons: false,
        streetViewControl: false
      })
    }
    else
    {
      map = this.state.map
    }
    return map
  }

  placeUserMarker(map, pos, pan=true)
  {
    if(this.state.userMarker != null)
    {
      this.state.userMarker.setMap(null);
    }

    let marker = new google.maps.Marker({
      position: pos,
      icon: './map/user_icon.svg',
      title: "Tu ubicación",
      draggable:false,
    })

    marker.setMap(map);

    if(pan)
    {
      this.panToPos(map, pos)
    }

    return marker
  }

  panToPos(map, pos, zoom=initialZoom)
  {
    map.setZoom(zoom);
    map.panTo(pos);
  }

  placeAllStoreMarkers(map, stores)
  {
    let list = []

    for (let i = 0; i < stores.length; i++) {
      let store = stores[i]
      if(store._id == null || store.lat == null || store.lng == null || store.name == null)
      {
        console.log("Skiped store: "+store.name)
        continue
      }

      let marker = this.placeAStoreMarker(map, store)
      list.push(marker)
    }

    return list
  }

  removeAllStoreMarkers()
  {
    for (let i = 0; i < this.state.storeMarkers.length; i++)
    {
      this.state.storeMarkers[i].setMap(null)
    }
  }

  removeSelectedStoreMarker()
  {
    this.state.selectedStore.setMap(null)
  }

  placeAStoreMarker(map, store, selected=false)
  {
    categories=[
      'pharmacy', 
      'bakery', 
      'vegetableStore', 
      'hardwareStore',
      'butchery', 
      'drycleaner',
      'stationeryStore',
      'petStore',
      'liquorStore',
      'fastFood',
      'restaurant',
      'groeceryStore',
      'store'
    ]

    labels=[
      'Droguería',
      'Panadería',
      'Fruver',
      'Ferretería',
      'Carnicería',
      'Lavandería',
      'Papelería',
      'Mascotas',
      'Licorera',
      'Comida rápida',
      'Restaurante',
      'Tienda',
      'Otro'
    ]

    if(store.type===null)
    {
      store.type="store"
    }

    if(!allCategories.includes(store.type))
    {
      store.type="store"
    }

    let i = categories.indexOf(store.type)
    store.typeLabel = label[i]

    let icon = '';
    if(selected)
    {
      icon = './map/' + store.type + "_icon_selected.svg";
    }
    else
    {
      icon = './map/' + store.type + "_icon.svg";
    }
    let icon_hover = './map/' + store.type + "_icon_hover.svg";

    let marker = new google.maps.Marker({
      position: {lat:store.lat, lng:store.lng},
      icon: icon,
      title:store.name,
      draggable:false,
      //custom data
      selected:selected,
      id:store._id,
      distance:distance(this.props.pos.lat, this.props.pos.lng, store.lat, store.lng)
    })

    google.maps.event.addListener(marker, 'click', function() {
      if(!marker.selected)
      {
        this.handleStoreMarkerClick(marker.id);
      }
    })

    google.maps.event.addListener(marker, 'mouseover', function() {
      marker.setIcon(icon_hover);
    })

    google.maps.event.addListener(marker, 'mouseout', function() {
      marker.setIcon(icon);    
    })

    marker.setMap(map)
    return marker
  }

  findStore(id)
  {
    for (let i = 0; i < this.props.stores.length; i++) {
      const element = this.props.stores[i];

      if(element._id === id)
      {
        return element
      }
    }
    return null
  }

  handleStoreMarkerClick(id)
  {
    alert("ya merito")
    let store = this.findStore()

    if(store !== null)
    {
      alert("ya mero merito")
      this.props.openSelectedStore(store)
    }
  }

  render () {
    let google_url = 'https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI'

    return (
      <div style={{height:"100%", width:'100%'}}>
        <Head>
          <script src={google_url} ></script>
        </Head>
        <div id="map" style={{height:"100%", width:'100%'}}></div>
      </div>
    )
  }
}

export default Map
