import Database from '../../middleware/mongodb.js'
import fetch from 'isomorphic-unfetch'

// The main, exported, function of the endpoint,
// dealing with the request and subsequent response
module.exports = async (req, res) => {
  // Get a database connection, cached or otherwise,
  // using the connection string environment variable as the argument
  const db = await Database()

  // Select the "stores" collection from the database
  const collection = await db.collection('stores')

  // Select the stores collection from the database
  const stores = await collection.find({ $or:[{lat:""}, {lng:""}] }).toArray()

  for (let index = 0; index < stores.length; index++) {
    const element = stores[index];
    if(element.address!="")
    {
        let address = element.address.replace(/\s/g, "+").toLowerCase()
        let url = process.env.GOOGLE_MAPS_API_URL + "?address="+address+"&key="+process.env.GOOGLE_MAPS_API_KEY

        let res = await fetch(url).then(function (response){
            if(response.status==200)
            {
                return response.json()
            }
            else{
                console.log(res.status)
            }
        })
    } 
  }

  // Respond with a JSON string of all stores in the collection
  res.status(200).json({ stores })
}
