import React from 'react';
import ReactDOM from 'react-dom';
import Head from 'next/head'

class Map extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      map: null,
      userMarker: null,
      stores: []
    }
  }

  static getDerivedStateFromProps(props, state) {
    let newMap = null
    let newUserMarker = null
    let newStores = []

    if(typeof google !== 'undefined' && props.pos != null && props.pos.lat != null && props.pos.lng != null) 
    {
      if(state.map == null)
      {
        newMap = new google.maps.Map(document.getElementById('map'), {
          center: props.pos,
          zoom: 15,
          mapTypeControl: false,
          fullscreenControl: false,
        })
      }
      else
      {
        newMap = state.map
      }

      if(this.state.userMarker != null)
      {
        this.state.userMarker.setMap(null);
      }

      newUserMarker = new google.maps.Marker({
        position: props.pos,
        icon: './map/user_icon.svg',
        title: "Tu ubicaci√≥n",
        draggable:false,
      })

      newUserMarker.setMap(newMap);
      newMap.panTo(prop.pos);

      if(Array.isArray(props.stores) && props.stores.length)
      {
        for (let i = 0; i < stores.length; i++) {
          store = stores[i]

          if(store.id == null || store.lat == null || store.lng == null || store.name == null)
          {
            console.log("Skiped store #"+i)
            continue
          }

          store.type = (store.type==null?"store":store.type.toLowerCase())
          let icon_normal = './map/' + store.type + "_icon.svg";
          let icon_hover = './map/' + store.type + "_icon_hover.svg";

          let marker = new google.maps.Marker({
            position: {lat:store.lat, lng:store.lng},
            icon: icon_normal,
            title:store.name,
            draggable:false,
            id:store.id, //custom data
          })

          google.maps.event.addListener(marker, 'click', function() {
            console.log("click " +marker.id)       
          })

          google.maps.event.addListener(marker, 'mouseover', function() {
            marker.setIcon(icon_hover);
          })

          google.maps.event.addListener(marker, 'mouseout', function() {
            marker.setIcon(icon_normal);    
          })

          newStores.push(marker)
        }

        setMapOnAll(newStores)
      }
      else
      {

      }
    }

    return {
      map: newMap,
      userMarker: newUserMarker,
      stores:newStores
    }
  }

  setStoresMarkers(stores, map)
  {
    for (let i = 0; i < stores.length; i++) {
      store = stores[i]

      if(store.id == null || store.lat == null || store.lng == null || store.name == null)
      {
        console.log("Skiped store #"+i)
        continue
      }

      store.type = (store.type==null?"store":store.type.toLowerCase())
      let icon_normal = './map/' + store.type + "_icon.svg";
      let icon_hover = './map/' + store.type + "_icon_hover.svg";

      let marker = new google.maps.Marker({
        position: {lat:store.lat, lng:store.lng},
        icon: icon_normal,
        title:store.name,
        draggable:false,
        id:store.id, //custom data
      })

      google.maps.event.addListener(marker, 'click', function() {
        console.log("click " +marker.id)       
      })

      google.maps.event.addListener(marker, 'mouseover', function() {
        marker.setIcon(icon_hover);
      })

      google.maps.event.addListener(marker, 'mouseout', function() {
        marker.setIcon(icon_normal);    
      })

      marker.setMap(map)
    }
  }

  render () {
    var customStyle = {
      padding:"0",
      margin:"0",
      display: 'block',
      height: '97vh',

      background:'WhiteSmoke'
    }

    return (
      <div>
        <Head>
          <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBt0ZCE1kAklBJiBnCYGX6kl0tglLcKlLI"></script>
        </Head>
        <div id="map" style={customStyle}>Cargando...</div>
      </div>
    )
  }
}

export default Map
