import React, { Component } from 'react'
import distance from './Distance'

import Dialog from '@material-ui/core/Dialog'
import Fab from '@material-ui/core/Fab'
import Button from '@material-ui/core/Button'
import Chip from '@material-ui/core/Chip'

import ArrowBackIcon from '@material-ui/icons/ArrowBack'
import DoneIcon from '@material-ui/icons/Done';

class SearchCard extends Component {

  constructor(props)
  {
    super(props)
    this.categories = ["Droguería", "Panadería", "Fruver", "Ferretería", "Carnicería", "Lavandería", "Papelería", "Mascotas", "Licores", "Comida Rápida", "Restaurante", "Tienda", "Supermercado"]
    this.state={
      stores:[],
      selectedCategories:this.categories
    }
  }

  handleClickFilter()
  {
    alert(":) ya mero mero")
  }

  handleChipClick(type)
  {
  }

  render() {

    return (
      <div>{(this.props.stores && (<div>
        <Dialog 
          aria-labelledby="simple-dialog-title" 
          open={this.props.open}
          fullScreen
          style={{height:"100%", position:"relative", background:'#ffcf02'}}
        >
            <div style={{height:'50%', background:'lightgrey'}}></div>

            <div style={{display:'relative'}}>
              <Fab size="small" aria-label="menu" onClick={() => this.props.close()} style={{position:"absolute", top:"10px", left:"15px"}}>
                <ArrowBackIcon />
              </Fab>

              <div style={{display:'flex', alignItems:'center', padding:'20px'}}>

              </div>
              <div style={{margin:'10px', textAlign:'center'}}>


                {this.categories.map((item) => (
                  <Chip
                    size="small"
                    deleteIcon={<DoneIcon />}
                    label={item}
                    clickable
                    onDelete={()=>this.handleChipClick(item)}
                    deleteIcon={<DoneIcon />}
                  />
                ))}

                <span>
                  <Button variant="contained" color="primary" onClick={()=>this.handleClickFilter()}><small>Filtrar</small></Button>
                </span>
              </div>
            </div>
        </Dialog>
      </div>))}
      </div>
    )
  }
}

export default SearchCard;
